mapper.select:
select f.flight_id, f.flight_number, f.flight_leave, f.flight_arrive, f.flight_leaveport, f.flight_arriveport, f.flight_leavetime, f.flight_arrivetime, f.flight_duration, f.flight_price, f.cabins_id, f.airlines_id, c.airline_id, c.airline_name, c.airline_logo, airline_model, airline_status, airline_update, t.cabin_id, t.cabin_name, t.cabin_status, t.cabin_update from `flight_records` f inner join `airline_company` c on f.airlines_id = c.airline_id inner join `cabin_type` t on f.cabins_id = t.cabin_id
AND f.flight_leave = #{flightLeave}
AND f.flight_arrive = #{flightArrive}
AND f.flight_leavetime >= #{flightLeaveTime}
AND str_to_date(f.flight_arrivetime, '%Y-%m-%d') = #{flightArriveTime}
AND f.flight_number = #{flightNumber}

select f.flight_id, f.flight_number, f.flight_leave, f.flight_arrive, f.flight_leaveport, f.flight_arriveport, f.flight_leavetime, f.flight_arrivetime, f.flight_duration, f.flight_price, f.cabins_id, f.airlines_id, c.airline_id, c.airline_name, c.airline_logo, airline_model, airline_status, airline_update, t.cabin_id, t.cabin_name, t.cabin_status, t.cabin_update from `flight_records` f inner join `airline_company` c on f.airlines_id = c.airline_id inner join `cabin_type` t on f.cabins_id = t.cabin_id
AND f.flight_leave = #{flightLeave}
AND f.flight_arrive = #{flightArrive}
AND f.flight_leavetime >= #{flightLeaveTime}
AND str_to_date(f.flight_arrivetime, '%Y-%m-%d') = #{flightArriveTime}
AND f.flight_number = #{flightNumber}
